changeSet.sql:
CREATE TABLE budget.user (
id INTEGER NOT NULL DEFAULT nextval('budget.user_seq'),
surname VARCHAR(100) NOT NULL,
name VARCHAR(100) NOT NULL,
email VARCHAR(100) NOT NULL,
password VARCHAR(100) NOT NULL,

CONSTRAINT user_id_pk PRIMARY KEY (id)
);

COMMENT ON TABLE budget.user IS 'List of users';
COMMENT ON COLUMN budget.user.id IS 'ID';
COMMENT ON COLUMN budget.user.surname IS 'Surname of user';
COMMENT ON COLUMN budget.user.name IS 'Name of user';
COMMENT ON COLUMN budget.user.email IS 'Email of user';
COMMENT ON COLUMN budget.user.password IS 'Password of user';
