changeSet.sql:
CREATE TABLE budget.account (
id INTEGER NOT NULL DEFAULT nextval('budget.account_seq'),
user_id INTEGER NOT NULL,
name VARCHAR(100) NOT NULL,
balance NUMERIC(10,2) NOT NULL,

CONSTRAINT account_id_pk PRIMARY KEY (id),
CONSTRAINT user_id_fk FOREIGN KEY (user_id) REFERENCES budget.user (id)
);

COMMENT ON TABLE budget.account IS 'List of accounts';
COMMENT ON COLUMN budget.account.id IS 'ID';
COMMENT ON COLUMN budget.account.user_id IS 'id of user';
COMMENT ON COLUMN budget.account.name IS 'Name of account';
COMMENT ON COLUMN budget.account.balance IS 'Balance of account';
