changeSet.sql:
CREATE TABLE budget.income (
id INTEGER NOT NULL DEFAULT nextval('budget.income_seq'),
account_id INTEGER NOT NULL,
date DATE NOT NULL,
sum NUMERIC(10, 2) NOT NULL,
description VARCHAR(100),

CONSTRAINT income_id_pk PRIMARY KEY (id),
CONSTRAINT account_id_fk FOREIGN KEY (account_id) REFERENCES budget.account (id)
);

COMMENT ON TABLE budget.income IS 'List of incomes';
COMMENT ON COLUMN budget.income.id IS 'ID';
COMMENT ON COLUMN budget.income.account_id IS 'id of account';;
COMMENT ON COLUMN budget.income.date IS 'Date of income';
COMMENT ON COLUMN budget.income.sum IS 'Sum of income';
COMMENT ON COLUMN budget.income.description IS 'Description';
