changeSet.sql:
CREATE TABLE budget.expensecategory (
id INTEGER NOT NULL DEFAULT nextval('budget.expensecategory_seq'),
account_id INTEGER NOT NULL,
category_id INTEGER NOT NULL,
date DATE NOT NULL,

CONSTRAINT expensecategory_id_pk PRIMARY KEY (id),
CONSTRAINT account_id_fk FOREIGN KEY (account_id) REFERENCES budget.account (id),
CONSTRAINT category_id_fk FOREIGN KEY (category_id) REFERENCES budget.category (id)
);

COMMENT ON TABLE budget.expensecategory IS 'List of expense by category';
COMMENT ON COLUMN budget.expensecategory.id IS 'ID';
COMMENT ON COLUMN budget.expensecategory.account_id IS 'id of account';
COMMENT ON COLUMN budget.expensecategory.category_id IS 'id of category';
COMMENT ON COLUMN budget.expensecategory.date IS 'Date expense';
