changeSet.sql:
CREATE TABLE budget.expensetype (
id INTEGER NOT NULL DEFAULT nextval('budget.expensetype_seq'),
expensecategory_id INTEGER NOT NULL,
type_id INTEGER,
sum NUMERIC(10, 2) NOT NULL,
description VARCHAR(100),

CONSTRAINT expensetype_id_pk PRIMARY KEY (id),
CONSTRAINT expensecategory_id_fk FOREIGN KEY (expensecategory_id) REFERENCES budget.expensecategory (id),
CONSTRAINT type_id_fk FOREIGN KEY (type_id) REFERENCES budget.type (id)
);

COMMENT ON TABLE budget.expensetype IS 'List of expense by type';
COMMENT ON COLUMN budget.expensetype.id IS 'ID';
COMMENT ON COLUMN budget.expensetype.expensecategory_id IS 'id of expense by category';
COMMENT ON COLUMN budget.expensetype.type_id IS 'id of type';
COMMENT ON COLUMN budget.expensetype.sum IS 'Sum of expense';
COMMENT ON COLUMN budget.expensetype.description IS 'Description';
